mapper.sql:
where 1=1

	
	    and role_id =  #{roleId}
	
	
	    and role_name =  #{roleName}
	
	
	    and remark =  #{remark}

mapper.update:
update roles set 


	role_name=#{roleName, jdbcType=VARCHAR},


	remark=#{remark, jdbcType=VARCHAR}

 where role_id=#{roleId}

mapper.select:
select  from roles
where 1=1

					
	    and role_id =  #{object.roleId}
	
					
	    and role_name =  #{object.roleName}
	
					
	    and remark =  #{object.remark}
	
	

	order by
	
		${item}

     SELECT
     count(*)
     FROM roles 
     where 1=1 
     
							
			    and role_id =  #{roleId}
			
							
			    and role_name =  #{roleName}
			
							
			    and remark =  #{remark}
