mapper.sql:
where 1=1

	
	    and id =  #{id}
	
	
	    and book_name =  #{bookName}
	
	
	    and transaction_xid =  #{transactionXid}

mapper.update:
update t_book set 


	book_name=#{bookName, jdbcType=VARCHAR},


	transaction_xid=#{transactionXid, jdbcType=VARCHAR}

 where id=#{id}

mapper.select:
select  from t_book
where 1=1

					
	    and id =  #{object.id}
	
					
	    and book_name =  #{object.bookName}
	
					
	    and transaction_xid =  #{object.transactionXid}
	
	

	order by
	
		${item}

     SELECT
     count(*)
     FROM t_book 
     where 1=1 
     
							
			    and id =  #{id}
			
							
			    and book_name =  #{bookName}
			
							
			    and transaction_xid =  #{transactionXid}
